mapper.select:
select goods_id as goodsId , SUM(actual_price) as goodsMpney from order_details
where (order_status>30 or order_status=30) and create_date > #{startDate} and create_date < #{endDate}
and is_deleted = 0 GROUP BY goodsId ORDER BY goodsMpney DESC LIMIT 10

select SUM(actual_price) from order_details
where (order_status>30 or order_status=30) and create_date > #{startDate} and create_date < #{endDate}
and is_deleted = 0

select goods_id as goodsId , COUNT(id) as goodsOrder from order_details
where (order_status>30 or order_status=30) and create_date > #{startDate} and create_date < #{endDate}
and is_deleted = 0 GROUP BY goodsId ORDER BY goodsOrder DESC LIMIT 10

select COUNT(id) from order_details
where (order_status>30 or order_status=30) and create_date > #{startDate} and create_date < #{endDate}
and is_deleted = 0
